import { Component } from '@angular/core';
import { Request2 } from 'src/app/models/request.model';
import { RequestService } from 'src/app/services/request.service';

@Component({
  selector: 'app-request',
  templateUrl: './request.component.html',
  styleUrls: ['./request.component.scss']
})
export class RequestComponent {
  name:string = '';
  requestToSave!:Request2;
  constructor(private requestService:RequestService){}
  createRequest(){
    myDate:Date = new Date('1999-10-10');
    this.requestToSave = {
      id:1,
    name:'Jose',
    date: "1999-12-10",
    subject:'un subject',
    description:'una descripcioin'
    };
    this.requestService.createRequest(this.requestToSave);  

  }

}
<form action="">
    <div class="row">
        <div class="col-xs-12">
            <div class="form-group">
                <label for="nombre">Nombre</label>
                <input [(ngModel)]="name" type="text" name="nombre" id="form__nombre" class="form-control">
            </div>
            <div class="form-group">
                <label for="date">Fecha</label>
                <input type="date" name="date" id="form__date">
            </div>
            <div class="form-group">
                <label for="subject">Tema</label>
                <input type="text" name="subject" id="form__subject">
            </div>
            <div class="form-group">
                <label for="description">Descripcion de la consulta</label>
                <textarea id="form__description" name="description" rows="4" cols="50"></textarea>                
            </div>
        </div>
    </div>
    <div class="row">
        <div class="col-xs-12">
            <button type="button">Resetear</button>
            <button type="button">Cancelar</button>
            <button (click)='createRequest()' type="submit">Enviar</button>
        </div>
    </div>
</form>
