import { Component } from '@angular/core';
import { Request2 } from 'src/app/models/request.model';
import { RequestService } from 'src/app/services/request.service';

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.scss']
})
export class ListComponent {
  requests!:Request2[];
  requestToSave!:Request2;
  constructor(private requestService:RequestService){}
  ngOnInit():void{
    this.requestService.getRequests().subscribe((data:Request2[])=>{
      this.requests = data;
    })
  }
  create(){
    // this.requestToSave = {
    //   id:1,
    //   name:"un nombre",
    //   subject:"un subject",
    //   date:new Date(),
    //   description:"una descripcion", 
    // } as Request2;
  }
}
<p>list works!</p>
<button (click)="create()">Crear</button>
<table class="table">

    <thead>
      <tr>
        <th>Nombre</th>
        <th>Subject</th>
        <th>Description</th>
        <th>Date</th>
      </tr>
    </thead>
<tbody>
    <tr *ngFor="let aRequest of requests">
      <td>{{ aRequest.name }}</td>
      <td>{{ aRequest.subject }}</td>
      <td>{{ aRequest.description }}</td>
      <td>{{ aRequest.date | date : "d/M/y" }}</td>
    </tr>
  </tbody>
</table>
