import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Request2 } from 'src/app/models/request.model';
import { RequestService } from 'src/app/services/request.service';

@Component({
  selector: 'app-request',
  templateUrl: './request.component.html',
  styleUrls: ['./request.component.scss']
})
export class RequestComponent {
  name:string = '';
  date:Date =new Date();
  subject:string = '';
  description:string = '';
  constructor(private requestService:RequestService,private router:Router){}
  createRequest(){
    
    let requestToSave = {
      id:1,
    name:this.name,
    subject:this.subject,
    description:this.description,
    date:new Date()
    };
    this.requestService.createRequest(requestToSave).subscribe(
      (response) => {
        console.log(response);
      },
      (error) => {
        console.log(error);
      }
    );
    this.router.navigate(['']);
  }
  redirectHome(){
    this.router.navigate(['']);
  }

}
<form action="">
    <div class="row">
        <div class="col-xs-12">
            <div class="form-group">
                <label for="nombre">Nombre</label>
                <input  type="text" name="name" id="form__nombre" class="form-control" [(ngModel)]="name">
            </div>
            <div class="form-group">
                <label for="date">Fecha</label>
                <input disabled type="date" name="date" id="form__date" [(ngModel)]="date">
            </div>
            <div class="form-group">
                <label for="subject">Tema</label>
                <input type="text" name="subject" id="form__subject" [(ngModel)]="subject">
            </div>
            <div class="form-group">
                <label for="description">Descripcion de la consulta</label>
                <textarea id="form__description" name="description" rows="4" cols="50" [(ngModel)]="description"></textarea>                
            </div>
        </div>
    </div>
    <div class="row">
        <div class="col-xs-12">
            <button type="button">Resetear</button>
            <button type="button">Cancelar</button>
            <button (click)='createRequest()' type="submit">Enviar</button>
        </div>
    </div>
</form>
