import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Request2 } from 'src/app/models/request.model';
import { RequestService } from 'src/app/services/request.service';

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.scss']
})
export class ListComponent {
  requests!:Request2[];
  requestToSave!:Request2;
  constructor(private requestService:RequestService, private router:Router){}
  ngOnInit():void{
    this.requestService.getRequests().subscribe((data:Request2[])=>{
      this.requests = data;
    })
  }
  create(){
    this.router.navigate(['create']);
  }
  edit(id:number){
    console.log("list.component.ts->editar id=" + id);
    this.router.navigate(['edit/'+ id]);
  }
  deleteRequest(id:number){

  }
}
<button class="margin-top-bottom button-padding" (click)="create()">Crear</button>
<table class="table">
  
    <!-- <thead>
      <tr>
        <th>Nombre</th>
        <th>Subject</th>
        <th>Description</th>
        <th>Date</th>
      </tr>
    </thead> -->
<tbody>
    <tr class="margin-top-bottom" *ngFor="let aRequest of requests">
      <td>{{ aRequest.name }}</td>
      <td>{{ aRequest.subject }}</td>
      <td>{{ aRequest.description }}</td>
      <td>{{ aRequest.date | date : "d/M/y" }}</td>
      <button class="margin-left "(click)="edit(aRequest.id)">Editar</button>
      <button class="margin-left "(click)="deleteRequest(aRequest.id)">Borrar</button>
    </tr>
  </tbody>
</table>
